import { useState } from 'react'
import { command } from './command'
import { handleCommand } from './phoneBook'


let id = 0

function getHistory() {
    const [historyElements, setHistoryElements] = useState([])
    function addHistoryElement() {
        id += 1
        setHistoryElements([
            ...historyElements,
            {id: id, command: command, result: handleCommand(command)}
        ])
        return historyElements
    }
    function deleteHistoryElement(element) {
      let elementId = element.target.id
      setHistoryElements(historyElements.filter(element => element.id !== Number(elementId)))
    }

    function deleteHistory() {
      setHistoryElements([])
    }

    function History() {
      return (
        <ul>
          {historyElements.map(element => (
            <div style={{display: 'flex'}}> 
              <li key={element.id}>
                <span class='green'>{element.command + ': '}</span>
                <span class='blue'>{element.result}</span>
              </li>
              <button
              id={element.id}
              onClick={deleteHistoryElement}>
                Delete history element
              </button>
            </div>
          ))}
        </ul>
      )
    } 
    return {
      addHistoryElement,
      deleteHistory,
      History
    }
}

export const addHistoryElement = getHistory().addHistoryElement()

export const History = getHistory().History()

export const deleteHistory = getHistory().deleteHistory()
