import './App.css'
import { useState } from 'react'
import { Input } from './components/Input.js'
import { useHistory } from './hooks/history'
import { History } from './components/History'
import { handleCommand, usePhoneBook } from './hooks/phoneBook'
import { getPhoneBook } from './hooks/phoneBook'
import { PhoneBook } from './components/PhoneBook'


export function App() {
    const [command, setCommand] = useState('')
    const phoneBook = usePhoneBook()
    const history = useHistory(command, phoneBook)

    console.log("RENDER");

    const perfomCommand = () => {
      const response = handleCommand(command, phoneBook)
      history.addElement(response)
    }

    return (
    <div class='App'>
      <History
        historyElements={history.elements}
        deleteHistoryElement={history.deleteElement}
      />
      <Input
        value={command}
        setValue={setCommand}
      />
      <button onClick={perfomCommand}>Enter</button>
      <button onClick={history.deleteHistory}>Delete history</button>
      <PhoneBook
        getPhoneBook={getPhoneBook}
        phoneBook={phoneBook}
      />
    </div>
  )
}

export default App
