import './App.css';
import { useState } from 'react'


function App() {

  const [command, setCommand] = useState('')
  const [historyElements,setHistoryElements] = useState([])
  let nowCommand = ''
  let nextId = 0
  const handleInput = (e) => {
    nowCommand = e.target.value
  }

  function handleClick() {
    setCommand(nowCommand)
    setHistoryElements([
      ...historyElements,
      {id: nextId++, text: nowCommand}
    ])
  }

  function History() {
    return (
      <ul>
        <li>{historyElements}</li>
      </ul>
    )
  }

  return (
    <div class='App'>
      <History/>
      <input onChange={handleInput}/>
      <button onClick={handleClick}>Enter</button>
    </div>
  )
}



export default App;
