import './App.css';
import { handleCommand, getPhoneBook } from './phoneBook'
import { useState } from 'react'

let id = 0


function History({ historyElements, setHistoryElements }) {

  function deleteHistoryElement(element) {
    let elementId = element.target.id
    setHistoryElements(historyElements.filter(element => element.id !== Number(elementId)))
  }

  return (
    <ul>
      {historyElements.map(element => (
        <div style={{display: 'flex'}}> 
          <li key={element.id}>
            <span class='green'>{element.command + ': '}</span>
            <span class='blue'>{element.result}</span>
            <span class='blue'>{element.id}</span>
          </li>
          <button
          id={element.id}
          onClick={deleteHistoryElement}>
            Delete history element
          </button>
        </div>
      ))}
    </ul>
  )
}

function PhoneBook() {
  return (
    <>
      <h2>Наша телефонная книга</h2>
      <ol>
        {getPhoneBook().map(element => (
          <li>
            <span className='green'>{element.name + ' - номер: '}</span>
            <span className='blue'>{element.number}</span>
          </li>
        ))}
      </ol>
    </>
  )
}

function App() {
  const [historyElements, setHistoryElements] = useState([])
