import './App.css';
import { handleCommand, getPhoneBook } from './phoneBook'
import { useState } from 'react'

function History({ historyElements, setHistoryElements }) {

  function deleteHistoryElement(element) {
    let elementId = element.target.id
    console.log([historyElements.map(e => e.id===Number(elementId)), elementId]);
    setHistoryElements(historyElements.filter(element => element.id !== Number(elementId)))
  }

  return (
    <ul>
      {historyElements.map(element => (
        <div>
          <li key={element.id}>
            <span class='green'>{element.command + ': '}</span>
            <span class='blue'>{element.result}</span>
          </li>
          <button 
          id={element.id}
          onClick={deleteHistoryElement}>
            Delete history element
          </button>
        </div>
      ))}
    </ul>
  )
}

function PhoneBook() {
  return (
    <>
      <h2>Наша телефонная книга</h2>
      <ol>
        {getPhoneBook().map(element => (
          <li>
            <span className='green'>{element.name + ' - номер: '}</span>
            <span className='blue'>{element.number}</span>
          </li>
        ))}
      </ol>
    </>
  )
}

function App() {
  const [historyElements, setHistoryElements] = useState([])
  const [command, setCommand] = useState('')
  const [id, setId] = useState(0)
  const handleInput = (e) => {
    setCommand(e.target.value)
  }

  function handleClick() {
    setId(id + 1)
    setHistoryElements([
      ...historyElements,
      {id: id, command: command, result: handleCommand(command)}
    ])
  }

  function deleteHistory() {
    setHistoryElements([])
  }

  return (
    <div class='App'>
      <History
        historyElements={historyElements}
        setHistoryElements={setHistoryElements}
      />
      <input onChange={handleInput}/>
      <button onClick={handleClick}>Enter</button>
      <button onClick={deleteHistory}>Delete history</button>
      <PhoneBook />
    </div>
  )
}



export default App;
